import {Container, Form, FormGroup} from "react-bootstrap";
import BarraBusca from "./BarraBusca";
import { useEffect, useState } from "react";
import { Cabecalho } from "./Templates/Cabecalho";

export default function Formulario(propd){
  const [lista, setLista] = useState([])

  useEffect(()=>{
    fetch("http://localhost:3002/motoristas", {method: "GET"}).then((resposta)=>{
      return resposta.json();
    }).then((dados)=>{
      setLista(dados);
    });
  },[])
  
  const [pessoaSlecionada, setPessoaSelecionada] = useState({});
    return (
        <Container>
            <Cabecalho texto="Sistema de Gestão"/>
            <h2 className="text-center ms-5">Cadastro</h2>
            <Form>
                <Form.Group className="mb-3" controlId="exampleForm.ControlInput1">
                    <Form.Label>Melhoria</Form.Label>
                    <Form.Control type="text" placeholder="" />
                </Form.Group>
                <Form.Group className="mb-3" controlId="">
                    <Form.Label>Descrição</Form.Label>
                    <Form.Control as="textarea" rows={3} />
                </Form.Group>
                <FormGroup>
                    <BarraBusca 
                        placeHolder={"Pesquise o autor da sugestão"}
                        dados={lista}
                        campoChave={"ID"}
                        campoBusca={"nome"}
                        funcaoSelecao={setPessoaSelecionada}
                        valor={""}>
                    </BarraBusca>
                </FormGroup>
            </Form>
        </Container>
    );

}
